package application;

public class SomaIndices {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int INDICE = 13, SOMA = 0, K = 0;

        while (K < INDICE) {
            K = K + 1;
            SOMA = SOMA + K;
        }

        System.out.println(SOMA);
    	}

	}

