import java.util.Scanner;

public class PrecoMetroArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		int a;
		a = sc.nextInt();
		
		int area = a * a;
		
		System.out.print("Area: " + area + " metros quadrados");
		
		sc.close();
		

	}

}
